import * as React from 'react';
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
// import TextField from '@mui/material/TextField';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import FormGroup from '@mui/material/FormGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Typography from '@mui/material/Typography';

import { useEth } from '../../contexts/EthContext';


export default function VoteDialog({ candidateName}) {

    const { state: { web3, contract } } = useEth();

    const [open, setOpen] = React.useState(false);
    const [userAgree, setUserAgree] = React.useState(false)


    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    const setVote = () => {


    }

    return (
        <div>
            <Button size="small" variant="contained" onClick={handleClickOpen}>
                Vote
            </Button>
            {/* <Button variant="outlined" onClick={handleClickOpen}>
                Open alert dialog
            </Button> */}
            <Dialog
                open={open}
                onClose={handleClose}
                aria-labelledby="alert-dialog-title"
                aria-describedby="alert-dialog-description"
                maxWidth="lg"
            >
                <DialogTitle id="alert-dialog-title">
                    Vote {candidateName}
                </DialogTitle>
                <DialogContent>
                    <DialogContentText id="alert-dialog-description">
                        Please confirm your vote <br /> <br />
                        <Typography color='red'>This action can't be undone</Typography>
                    </DialogContentText>
                    <FormGroup>
                        <FormControlLabel control={<Checkbox  onChange={ (e) => {
                            e.preventDefault();
                            console.log(e.target.checked);
                            setUserAgree(e.target.checked ? true:false);
                        }}/>} label="I consent to place my vote" />
                        {/* <FormControlLabel disabled control={<Checkbox />} label="Disabled" /> */}
                    </FormGroup>
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleClose}>Cancel</Button>
                    <Button
                        variant='contained'
                        disabled={!userAgree}
                        >
                        Confirm Vote
                    </Button>
                </DialogActions>
            </Dialog>
        </div>
    );
}
