  return (
    <>
      <div> Candidates</div>
      <div style={{display: 'block', justifyContent:'center'}}>
        {candidates.map((candidate) =>
        (<CandidateCard
          key={candidate.name}
          name={candidate.name}
          slogan={candidate.slogan} />))
        }
      </div>

    </>
  )
}

