import * as React from 'react';
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
// import TextField from '@mui/material/TextField';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import FormGroup from '@mui/material/FormGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Typography from '@mui/material/Typography';
import Alert from '@mui/material/Alert';

import { useEth } from '../../contexts/EthContext';


export default function ResultsDialog({ candidateName }) {

    const { state: { web3, contract } } = useEth();

    const [loading, setLoading] = React.useState(true);
    const [open, setOpen] = React.useState(false);
    const [results, setResults] = React.useState([])


    const resultsFunction = async () => {
        const candidatesCount = await contract.methods.getCandidatesCount().call();

        for (let i = 1; i <= candidatesCount; i++) {
            const candidate = await contract.methods.candidates(i).call();
            const cName = candidate[1];
            const cVoteCount = candidate[3];
            setResults([...results, {cName: cName, cVoteCount: cVoteCount}]);
            // setCandidates(candidates
            //     .push({ id: cId, name: cName, slogan: cSlogan, voteCount: cVoteCount}));           
        }
        setLoading(false);

    }

    React.useEffect( () => {
        resultsFunction();
    }, []);



    const handleClickOpen = () => {
        setOpen(true);
        resultsFunction();
    };

    const handleClose = () => {
        setOpen(false);
    };

    // const handleVote = async () => {


    // }

    return (
        <div>
            <Button size="small" variant="contained" onClick={handleClickOpen}>
                Get Voting Results!
            </Button>
            {/* <Button variant="outlined" onClick={handleClickOpen}>
                Open alert dialog
            </Button> */}
            <Dialog
                open={open}
                onClose={handleClose}
                aria-labelledby="alert-dialog-title"
                aria-describedby="alert-dialog-description"
                maxWidth="lg"
                PaperProps={{ sx: { width: '22%' } }}
            >
                <DialogTitle id="alert-dialog-title">
                    Elections 2022 Results
                </DialogTitle>

                { loading ? (<h2> results[0].cName </h2>) : <h2> loading !</h2> }

                {/* <DialogContent>
                    <DialogContentText id="alert-dialog-description">
                        PLACEHOLDER
                    </DialogContentText>

                </DialogContent> */}
            </Dialog>
        </div>
    );
}
