import * as React from 'react';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import Menu from '@mui/material/Menu';
// import MenuIcon from '@mui/icons-material/Menu';
import Container from '@mui/material/Container';
// import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import Tooltip from '@mui/material/Tooltip';
import MenuItem from '@mui/material/MenuItem';
// import AdbIcon from '@mui/icons-material/Adb';
import AdminPanelSettingsIcon from '@mui/icons-material/AdminPanelSettings';


import useEth from "../contexts/EthContext/useEth";
import useCandidates from "../contexts/CandidatesContext/useCandidates";



const settings = ['Add Candidate', 'Open Voting', 'Close Voting', 'isVotingOpen'];

const ResponsiveAppBar = () => {
    // const [anchorElNav, setAnchorElNav] = React.useState(null);
    const [anchorElUser, setAnchorElUser] = React.useState(null);


    const { state: { web3, contract } } = useEth();
    const  { candidates, setCandidates } = useCandidates();


    const handleOpenUserMenu = (event) => {
        setAnchorElUser(event.currentTarget);
    };





    const handleCloseUserMenu = (setting) => {
        setAnchorElUser(null);
        switch (setting){
            case 'Add Candidate': 
                console.log("add candidate");
                // TODO add candidate page (FORM)

                contract.methods.addCandidate('Bibi Nethanyahu', 'Rak bibi').send({ from: web3.eth.defaultAccount}).then( (result) =>{
                    console.log('candidate has been added');
                })

                console.log(candidates);
                // console.log(candidates);
                setCandidates([]);
                // getCandidates();
                break;
            case 'Open Voting':
                console.log("open voting");
                // settings.pop()
                // settings.push('Close Voting');

                contract.methods.toggleIsVotingOpen().send({ from: web3.eth.defaultAccount }).then(()=> {
                    console.log('toggled');
                });

                break;
            case 'isVotingOpen':
                const isVotingOpen = contract.methods.getIsVotingOpenValue().call().then( (res) => {
                    console.log(res);
                })
                break;
            case 'Close Voting':
                console.log("close voting");
                // settings.pop()
                // settings.push('Open Voting');

                contract.methods.toggleIsVotingOpen().send({ from: web3.eth.defaultAccount }).then(() => {
                    console.log('toggled');
                });

                break;
            default:
                break;
        }

    };

    return (
        <AppBar position="static">
            <Container maxWidth="xl">
                <Toolbar disableGutters>
                    <Typography
                        variant="h3"
                        noWrap
                        component="a"
                        href="/"
                        sx={{
                            mr: 2,
                            display: { xs: 'none', md: 'flex' },
                            fontFamily: 'monospace',
                            fontWeight: 700,
                            letterSpacing: '.23rem',
                            color: 'inherit',
                            textDecoration: 'none',
                        }}
                    >
                        ELECTIONS 2022
                    </Typography>           
                    <Box sx={{ flexGrow: 1, display: { xs: 'none', md: 'flex' } }}/>
                    <Box >
                        <Button style={{background:'black', margin:'10px', fontSize:'small', color:'white'}}> Get Voting Card! </Button>
                        </Box> 
                    <Box sx={{ flexGrow: 0 }}>
                        <Tooltip title="Admin Panel" sx={{ mt: '-10px' }}>
                            <IconButton onClick={handleOpenUserMenu} sx={{ p: 0 }}>
                                <AdminPanelSettingsIcon fontSize='large'/>
                            </IconButton>
                        </Tooltip>
                        <Menu
                            sx={{ mt: '30px' }}
                            id="menu-appbar"
                            anchorEl={anchorElUser}
                            anchorOrigin={{
                                vertical: 'top',
                                horizontal: 'right',
                            }}
                            keepMounted
                            transformOrigin={{
                                vertical: 'top',
                                horizontal: 'right',
                            }}
                            open={Boolean(anchorElUser)}
                            onClose={handleCloseUserMenu}
                        >
                            {settings.map((setting) => (
                                <MenuItem key={setting} onClick={ ()=> handleCloseUserMenu(setting)} 
                                    sx={{ backgroundColor: 'inherit'}}>
                                    <Typography textAlign="center" fontSize="small">{setting}</Typography>
                                </MenuItem>
                            ))}
                        </Menu>
                    </Box>
                </Toolbar>
            </Container>
        </AppBar>
    );
};
export default ResponsiveAppBar;
